def findMax(nums):
    if not nums:
        return None

    max_val = nums[0]

    for num in nums[1:]:
        if num > max_val:
            max_val = num

    return max_val

# Alternative: return max(nums)

# Time: O(n), Space: O(1)
# Linear scan approach

def removeDuplicates(nums):
    if not nums:
        return 0

    write_index = 1

    for i in range(1, len(nums)):
        if nums[i] != nums[i - 1]:
            nums[write_index] = nums[i]
            write_index += 1

    return write_index

# Time: O(n), Space: O(1)
# Two-pointer technique

def secondLargest(nums):
    if len(nums) < 2:
        return -1

    first = second = float('-inf')

    for num in nums:
        if num > first:
            second = first
            first = num
        elif num > second and num != first:
            second = num

    return second if second != float('-inf') else -1

# Time: O(n), Space: O(1)
# Single pass approach

def binarySearch(nums, target):
    left, right = 0, len(nums) - 1

    while left <= right:
        mid = (left + right) // 2

        if nums[mid] == target:
            return mid
        elif nums[mid] < target:
            left = mid + 1
        else:
            right = mid - 1

    return -1

# Time: O(log n), Space: O(1)
# Classic binary search

