def countFrequency(nums):
    from collections import Counter
    return dict(Counter(nums))

# Alternative without Counter:
def countFrequency(nums):
    freq = {}
    for num in nums:
        freq[num] = freq.get(num, 0) + 1
    return freq

# Time: O(n), Space: O(n)
# Hash map counting

def moveZeros(nums):
    write_index = 0

    # Move non-zero elements forward
    for i in range(len(nums)):
        if nums[i] != 0:
            nums[write_index] = nums[i]
            write_index += 1

    # Fill remaining with zeros
    for i in range(write_index, len(nums)):
        nums[i] = 0

# Time: O(n), Space: O(1)
# Two-pointer approach

def isSorted(nums):
    for i in range(1, len(nums)):
        if nums[i] < nums[i - 1]:
            return False
    return True

# Alternative: return nums == sorted(nums)

# Time: O(n), Space: O(1)
