from collections import deque

class MyStack:
    def init(self):
        self.q1 = deque()
        self.q2 = deque()

    def push(self, x):
        self.q1.append(x)

    def pop(self):
        # Move all except last to q2
        while len(self.q1) > 1:
            self.q2.append(self.q1.popleft())

        result = self.q1.popleft()
        # Swap queues
        self.q1, self.q2 = self.q2, self.q1
        return result

    def top(self):
        while len(self.q1) > 1:
            self.q2.append(self.q1.popleft())

        result = self.q1[0]
        self.q2.append(self.q1.popleft())
        self.q1, self.q2 = self.q2, self.q1
        return result

    def empty(self):
        return len(self.q1) == 0

# Push: O(1), Pop/Top: O(n)
